<?php
	
	$site = 'data:image/png;base64,';
	require_once("include/config.php");
	$_SESSION['msg'] = "";
	$_SESSION['set'] = "";
	if(isset($_GET['auth']))
	{
		$token = $_GET['auth'];
		$query=$con->prepare("SELECT * FROM users WHERE auth = '$token' ");
		$query->execute();
		
		$no=$query->rowCount();
		if($no==1)
		{
			if(isset($_POST['submit']))
			{
				$pw = hash("sha512", $_POST['pw']);
				$cpw = hash("sha512", $_POST['cpw']);
				if($pw === $cpw)
				{
					$up_query=$con->prepare("UPDATE users SET password = '$pw' where auth = '$token' ");
					$up_query->execute();
					if($up_query){
						$_SESSION['set'] = "password update succefully ";
						// header('location:signlog.php');
					}
					else{
						$_SESSION['msg'] = "Something worng try again";
					}
				}
				else{
					$_SESSION['msg'] = "Password Doesn't match";
				}
			}
		}
		else
		{
			header('location:index.php');
		}
	}
	else
	{
		header('location:index.php');
	}
?>

